mapper.select:
  
    SELECT t.id, t.uid, t.username, t.type, t.action, t.result, t.info, t.createtime FROM t_tgks_common_systemlog t


        1=1
    
        AND t.ID = #{id}
    
    
        AND t.UID = #{uid}
    
    
        AND t.NAME LIKE CONCAT(CONCAT('%', #{username}),'%')
    
    
        AND t.TYPE = #{type}
    
    
        AND t.ACTION = #{action}
    
    
        AND t.RESULT = #{result}
    
    
        AND t.CREATETIME >= #{createTimeStart}
    
    
        AND #{createTimeEnd} >= t.CREATETIME
    


    ORDER BY ${sortSql}
