mapper.select:
  
    SELECT t.id, t.tgksid, t.iosuuid, t.androiduuid, t.ioskey, t.androidkey, t.type, t.status, t.sessionid, t.vip, t.name, t.userid, t.lv, t.ap, t.apmax, t.bp, t.bpmax, t.cardnum, t.cardmax, t.gold, t.fp, t.coin, t.bossids, t.createtime, t.endtime, t.remark FROM t_tgks_mar_marzaccount t


        1=1
    
        AND t.ID = #{id}
    
    
        AND t.TGKSID = #{tgksId}
    
    
        AND t.IOSUUID = #{iosUuid}
    
    
        AND t.ANDROIDUUID = #{androidUuid}
    
    
        AND t.IOSKEY = #{iosKey}
    
    
        AND t.ANDROIDKEY = #{androidKey}
    
    
        AND t.TYPE = #{type}
    
    
        AND t.STATUS = #{status}
    
    
        AND t.SESSIONID = #{sessionId}
    
    
        AND t.VIP = #{vip}
    
    
        AND t.NAME = #{name}
    
    
        AND t.USERID = #{userId}
    
    
        AND t.LV = #{lv}
    
    
        AND t.AP = #{ap}
    
    
        AND t.APMAX = #{apMax}
    
    
        AND t.BP = #{bp}
    
    
        AND t.BPMAX = #{bpMax}
    
    
        AND t.CARDNUM = #{cardNum}
    
    
        AND t.CARDMAX = #{cardMax}
    
    
        AND t.GOLD = #{gold}
    
    
        AND t.FP = #{fp}
    
    
        AND t.COIN = #{coin}
    
    
        AND t.ENDTIME > #{endTime}
    


    ORDER BY ${sortSql}
